import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import toast from "react-hot-toast";
import "./Employee.css"

const Employee = () => {
    const [Employee, setEmployee] = useState([]);


    useEffect(()=>{
        const fetchData = async()=>{
            const response =await axios.get("http://localhost:8080/api/getall");
            setEmployee(response.data)
        }
    fetchData();
},[])
    const deleteEmp = async (userId) => {
        await axios.delete(`http://localhost:8080/api/delete/${userId}`)
        .then((response)=>{
            setEmployee((prevEmployee)=>prevEmployee.filter((Employee)=>Employee._id !== userId))
            toast.success(response.data.msg,{position:"top-right"})
        })
        .catch((err)=>console.log(err))
    }

    return(

        <div className="empTable">
            <Link to={"/"}>
                    <button className="backbtn">logout</button>
            </Link>
            <Link to={"/add"}>
            <button className="addbtn">Add User</button>
            </Link>
            <table border={1} cellPadding={10} cellSpacing={0}>
                <thead>
                    <tr>
                        <th>Unique Id</th>
                        <th>image</th>
                        <th>Name</th>
                        <th>Email </th>
                        <th>Mobile</th>
                        <th>Designation</th>
                        <th>Gender</th>
                        <th>Course</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {
                        Employee.map((user,index)=>{
                            return(
                            <tr key={user._id}>
                                <td>{index+1}</td>
                                <td>NA</td>
                                <td>{user.Name}</td>
                                <td>{user.Email}</td>
                                <td>{user.Mobile}</td>
                                <td>{user.Designation}</td>
                                <td>{user.Gender}</td>
                                <td>{user.Course}</td>
                                <td className="actionbtn">
                                    <Link to={`/edit/`+user._id}>
                                        <button className="e">Edit</button>
                                    </Link>
                                    <button className="d" onClick={()=>deleteEmp(user._id)}>Delete</button>
                                </td>
                            </tr>
                        ) 
                    })
                    }
                    
                </tbody>
            </table>
        </div>
    )
}

export default Employee;
