import React from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import { useState } from "react";
import "./addEmp.css"

const AddEmp =() =>{
    const [data, setData] = useState({
		Name: "",
		email: "",
		mobile: "",
		designation:"",
		gender:"",
		course:"",
		img:""
	});
	const handleChange = ({ currentTarget: input }) => {
		if (input.type === "checkbox") {
		   let updatedCourses = [...data.course];
		   if (input.checked) {
			  updatedCourses.push(input.value);
		   } else {
			  updatedCourses = updatedCourses.filter((course) => course !== input.value);
		   }
		   setData({ ...data, course: updatedCourses });
		} else {
		   setData({ ...data, [input.name]: input.value });
		}
	 };
	 
	const handleSubmit = async (e) => {
		e.preventDefault();
		await axios.post("http://localhost:8080/api/add",data)
        .then((response)=>{
            console.log(response)
        }).catch(err =>console.log(err))
	  };
    return (
        <div className="addEmp">
            <Link to={"/"}>
                    <button className="backbtn">Back</button>
            </Link>
            <h3>Add New Employee</h3>
                <form className="form_container" onSubmit={handleSubmit}>
						<div className="inputGroup">
                        <label htmlFor="Name"> Full Name </label>
						<input
							type="text"
							placeholder="Enter Your Name"
							name="Name"
							onChange={handleChange}
							value={data.Name}
							required
							className="input"
						/>
                        </div>
                        <div className="inputGroup">
						<label htmlFor="email">Email</label>
						<input
							type="email"
							placeholder="Enter Email"
							name="email"
							onChange={handleChange}
							value={data.email}
							required
							className="input"
						/>
						</div>
                        <div className="inputGroup">
						<label htmlFor="mobil">Mobile</label>
						<input
							type="text"
							placeholder="Enter mobile number"
							name="mobile"
							onChange={handleChange}
							value={data.mobile}
							required
							className="input"
						/>
						</div>
                        <div className="inputOther">
						<label htmlFor="designation">Designation</label>
						
						<select
    						name="designation"
    						onChange={handleChange}
    						value={data.designation}
    						required
    						className="input"
  						>
    					<option value="">Select Designation</option>
    					<option value="HR">HR</option>
    					<option value="Manager">Manager</option>
    					<option value="Sales">Sales</option>
  						</select>
						</div>
                        <div className="inputOther">
						<label>Gender</label>
						<input
							type="radio"
							name="gender"
							onChange={handleChange}
							checked={data.gender==="Male"}
							value="Male"
							required
							className="input"
						/>
						<span>Male</span>
						<input
							type="radio"
							name="gender"
							onChange={handleChange}
							checked={data.gender==="Female"}
							value="Female"
							required
							className="input"
						/>
						<span>Female</span>
						</div>
                        <div className="inputOther">
						<label>Course</label>
						<input
							type="checkbox"
							name="course"
							value="MCA"
							checked={data.course === 'MCA'}
							onChange={handleChange}
							className="input"
						/>
						<span>MCA</span>
						<input
							type="checkbox"
          					name="course"
          					value="BCA"
          					checked={data.course === 'BCA'}
          					onChange={handleChange}
          					className="input"
						/>
						<span>BCA</span>
						<input
							type="checkbox"
							name="course"
							value="BSC"
							checked={data.course === 'BSC'}
							onChange={handleChange}
							className="input"
						/>
						<span>BSC</span>
						</div>
                        <div className="inputOther">
						<label>Photo</label>
						<input
						type="String"
						name="img"
						onChange={handleChange}
						/>
						
						</div>
						<div className="inputbtn">
						<button type="submit" className="green_btn">
							Add User
						</button>
                        </div>
					</form>
        </div>
    )
}

export default AddEmp;
